import React, { useState } from 'react';
import { QrReader } from 'react-qr-reader';
import axios from 'axios';
import './scanner.css';

const QRCodeScanner = () => {
  const [scanResult, setScanResult] = useState(null);
  const [details, setDetails] = useState(null);
  const [error, setError] = useState('');

  const handleScan = async (data) => {
    console.log('QR Code Data:', data);
  
    if (data) {
      try {
        // Assuming data is a comma-separated string
        const [name, place, phone, source, membersJson, occupation] = data.split(', ');
  
        // Parse membersJson if it's in JSON format
        const members = JSON.parse(membersJson || '[]');
  
        // Format data as needed
        const formattedData = {
          name,
          place,
          phone,
          source,
          occupation,
          members: members.map(member => ({
            ...member,
            // Format each member if needed
          }))
        };
  
        setScanResult(formattedData);
  
        // Optionally fetch details from the backend
        const response = await axios.get('http://localhost:4000/api/customer/by-qrcode', {
          params: { qrCodeData: data }
        });
        setDetails(response.data);
  
      } catch (err) {
        setError('Error processing QR code data');
        console.error('Error processing QR code data:', err);
      }
    }
  };
  

  const handleError = (error) => {
    // setError('Error scanning QR code');
    console.error('Error scanning QR code:', error);
  };

  return (
    <div className="scanner-container">
      <h1>QR Code Scanner</h1>
      <QrReader
        onResult={(result, error) => {
          if (result) {
            handleScan(result.text);
          }
          if (error) {
            handleError(error);
          }
        }}
        style={{ width: '100%' }}
      />

      {error && <p className="error-message">{error}</p>}

      {details && (
        <div className="details-container">
          <h2>Customer Details</h2>
          <pre>{JSON.stringify(details, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default QRCodeScanner;
