import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./RegistrationForm.css";

const InitialForm = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Navigate to the ExistingUserForm with query parameters
    navigate(`/existing-user?name=${encodeURIComponent(name)}&phone=${encodeURIComponent(phone)}`);
  };

  return (
    <div className="form-container">
      <header className="header">
        <img src={AAPLlogo} alt="Header" className="header-image" />
        <div className="logo-container">
          <img src={logo} alt="Logo" className="header-logo" />
        </div>
        <div className="header-middle">
          <h1>IBIZ Registration Form</h1>
        </div>
      </header>
      <form onSubmit={handleSubmit} className="registration-form">
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label>
          Phone No:
          <input
            type="text"
            name="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </label>
        <button type="submit" className="submit-btn">
          Next
        </button>
      </form>
    </div>
  );
};

export default InitialForm;
