import React, { useState } from "react";
import axios from "axios";
import "./RegistrationForm.css";
import AAPLlogo from "../assets/img.png";
import logo from "../assets/img1.jpeg";
import { Link } from "react-router-dom";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    place: "",
    phone: "",
    source: "",
    members: 0,
    occupation: "",
    age: 0,
    gender: "",
    member: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:4000/api/customers",
        formData
      );
      alert("Form submitted successfully!");
      setFormData({
        name: "",
        place: "",
        phone: "",
        source: "",
        members: 0,
        occupation: "",
        age: 0,
        gender: "",
        member: "",
      });
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  return (
    <div className="form-container">
      <header className="header">
        <img src={AAPLlogo} alt="Header" className="header-image" />
        <div className="logo-container">
          <img src={logo} alt="Logo" className="header-logo" />
        </div>
        <div className="header-middle">
          <h1>IBIZ Registration Form</h1>
        </div>
        <div className="header-middle">
          <Link to={`/existing-user`} className="existing-user-link">
            <h3>If Already Registered Click Here</h3>
          </Link>
        </div>
      </header>
      <form onSubmit={handleSubmit} className="registration-form">
        <fieldset className="gender-fieldset">
          <legend>JESA Member:</legend>
          <label>
            <input
              type="radio"
              name="member"
              value="Yes"
              checked={formData.member === "Yes"}
              onChange={handleChange}
            />
            Yes
          </label>
          <label>
            <input
              type="radio"
              name="member"
              value="No"
              checked={formData.member === "No"}
              onChange={handleChange}
            />
            No
          </label>
        </fieldset>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <fieldset className="gender-fieldset">
          <legend>Gender:</legend>
          <label>
            <input
              type="radio"
              name="gender"
              value="Male"
              checked={formData.gender === "Male"}
              onChange={handleChange}
            />
            Male
          </label>
          <label>
            <input
              type="radio"
              name="gender"
              value="Female"
              checked={formData.gender === "Female"}
              onChange={handleChange}
            />
            Female
          </label>
          <label>
            <input
              type="radio"
              name="gender"
              value="Other"
              checked={formData.gender === "Other"}
              onChange={handleChange}
            />
            Other
          </label>
        </fieldset>
        <label>
          Place:
          <input
            type="text"
            name="place"
            value={formData.place}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Phone No:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Age:
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Where you got to know about IBiz:
          <select
            name="source"
            value={formData.source}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="Social Media">Social Media</option>
            <option value="Friend">Friend</option>
            <option value="Advertisement">Advertisement</option>
            <option value="Event">Event</option>
            <option value="FM">FM</option>
          </select>
        </label>
        <label>
          Members with you:
          <input
            type="number"
            name="members"
            value={formData.members}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Occupation:
          <select
            name="occupation"
            value={formData.occupation}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="Student">Student</option>
            <option value="Business">Business</option>
            <option value="Job">Job</option>
            <option value="Other">Other</option>
          </select>
        </label>
        <button type="submit" className="submit-btn">
          Submit
        </button>
      </form>
    </div>
  );
};

export default RegistrationForm;
