// src/components/RegistrationForm.js
import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    place: '',
    phone: '',
    source: '',
    members: '',
    occupation: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/customers', formData);
      alert('Form submitted successfully!');
      setFormData({
        name: '',
        place: '',
        phone: '',
        source: '',
        members: '',
        occupation: ''
      });
    } catch (error) {
      alert('Error submitting form');
    }
  };

  return (
    <div className="form-container">
      <header>
        <div className="header-content">
          <h1>Jain Engineer's Society Aurangabad</h1>
          <h2>JESA JESA-Nx</h2>
          <h3>Startup Edge: Mastering AI-Driven Video Creation</h3>
        </div>
      </header>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>
          Place:
          <input type="text" name="place" value={formData.place} onChange={handleChange} required />
        </label>
        <label>
          Phone No:
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />
        </label>
        <label>
          Where you got to know about IBiz:
          <input type="text" name="source" value={formData.source} onChange={handleChange} required />
        </label>
        <label>
          Members with you:
          <input type="number" name="members" value={formData.members} onChange={handleChange} required />
        </label>
        <label>
          Occupation:
          <input type="text" name="occupation" value={formData.occupation} onChange={handleChange} required />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default RegistrationForm;
