import React, { useState } from "react";
import axios from "axios";
import "./RegistrationForm.css"; // Import the new CSS file
import AAPLlogo from "../assets/img.png";
import logo from "../assets/logo";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    place: "",
    phone: "",
    source: "",
    members: "",
    occupation: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:4000/api/customers",
        formData
      );
      alert("Form submitted successfully!");
      setFormData({
        name: "",
        place: "",
        phone: "",
        source: "",
        members: "",
        occupation: "",
      });
    } catch (error) {
      alert("Error submitting form");
    }
  };

  return (
    <div className="form-container">
      <header className="header">
        <img src={AAPLlogo} alt="Full Width Header" className="header-image" />
        <div className="header-content">
          <div className="header-top">
            <div className="header-logo">
              {/* <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdY4U3hWMpmts0iOuvPulvNNr-af-zy85dgQ&s" alt="Left Logo" /> */}
            </div>
            <div className="header-title">
              {/* <h1>Jain Engineer's Society Aurangabad</h1> */}
              <img src={logo} alt="" className="" />
            </div>
            <div className="header-logo">
              <img src="/path-to-your-right-logo.png" alt="Right Logo" />
            </div>
          </div>
          <div className="header-middle">
            <h3>IBIZ Registration form</h3>
          </div>
        </div>
      </header>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Place:
          <input
            type="text"
            name="place"
            value={formData.place}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Phone No:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Where you got to know about IBiz:
          <input
            type="text"
            name="source"
            value={formData.source}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Members with you:
          <input
            type="number"
            name="members"
            value={formData.members}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Occupation:
          <input
            type="text"
            name="occupation"
            value={formData.occupation}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit" className="submit-btn">
          Submit
        </button>
      </form>
    </div>
  );
};

export default RegistrationForm;
