import React, { useState } from "react";
import axios from "axios";
import "./RegistrationForm.css";
import AAPLlogo from "../assets/img.png";
import logo from "../assets/img1.jpeg";

const ExistingUserForm = () => {
  const [initialFormData, setInitialFormData] = useState({
    name: "",
    phone: ""
  });
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    place: "",
    source: "",
    members: "",
    occupation: "",
    age: "",
    gender: ""
  });
  const [qrCodeUrl, setQrCodeUrl] = useState("");

  // Handle changes in the initial form
  const handleInitialChange = (e) => {
    setInitialFormData({
      ...initialFormData,
      [e.target.name]: e.target.value
    });
  };

  // Handle submission of the initial form
  const handleInitialSubmit = async (e) => {
    e.preventDefault();
    try {
      const { name, phone } = initialFormData;
      const response = await axios.get(`http://localhost:4000/api/customer/by-name-phone`, {
        params: { name, phone }
      });
      const customer = response.data;
  
      console.log('Fetched customer data:', customer); // Debug log
  
      setFormData(customer);
      setQrCodeUrl(customer.qrCodeUrl); // Update QR code URL
    } catch (error) {
      alert("Error fetching customer data");
      console.error('Error fetching customer data:', error); // Debug log
    }
  };

  // Handle changes in the main form
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Handle submission of the main form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { _id, ...updatedData } = formData;
      await axios.put(
        `http://localhost:4000/api/customers/${_id}`,
        updatedData
      );
      alert("Form updated successfully!");
    } catch (error) {
      alert("Error updating form");
    }
  };

  return (
    <div className="form-container">
      <header className="header">
        <img src={AAPLlogo} alt="Header" className="header-image" />
        <div className="logo-container">
          <img src={logo} alt="Logo" className="header-logo" />
        </div>
        <div className="header-middle">
          <h1>IBIZ Registration Form</h1>
        </div>
        <div className="header-middle">
          <h3>If Already Registered</h3>
        </div>
      </header>

      {/* Initial Form to Get Customer Data */}
      <form onSubmit={handleInitialSubmit} className="initial-form">
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={initialFormData.name}
            onChange={handleInitialChange}
            required
          />
        </label>
        <label>
          Phone No:
          <input
            type="text"
            name="phone"
            value={initialFormData.phone}
            onChange={handleInitialChange}
            required
          />
        </label>
        <button type="submit" className="submit-btn">
          Get Data
        </button>
      </form>

      {/* Form to Display and Update Customer Data */}
      {formData.name && (
        <form onSubmit={handleSubmit} className="registration-form">
          <fieldset className="gender-fieldset">
            <legend>Gender:</legend>
            <label>
              <input
                type="radio"
                name="gender"
                value="Male"
                checked={formData.gender === "Male"}
                onChange={handleChange}
              />
              Male
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="Female"
                checked={formData.gender === "Female"}
                onChange={handleChange}
              />
              Female
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="Other"
                checked={formData.gender === "Other"}
                onChange={handleChange}
              />
              Other
            </label>
          </fieldset>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Place:
            <input
              type="text"
              name="place"
              value={formData.place}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Phone No:
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Age:
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Where you got to know about IBiz:
            <select
              name="source"
              value={formData.source}
              onChange={handleChange}
              required
            >
              <option value="">Select</option>
              <option value="Social Media">Social Media</option>
              <option value="Friend">Friend</option>
              <option value="Advertisement">Advertisement</option>
              <option value="Event">Event</option>
              <option value="FM">FM</option>
            </select>
          </label>
          <label>
            Members with you:
            <input
              type="number"
              name="members"
              value={formData.members}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Occupation:
            <select
              name="occupation"
              value={formData.occupation}
              onChange={handleChange}
              required
            >
              <option value="">Select</option>
              <option value="Student">Student</option>
              <option value="Business">Business</option>
              <option value="Job">Job</option>
              <option value="Other">Other</option>
            </select>
          </label>
          <button type="submit" className="submit-btn">
            Submit
          </button>
          {qrCodeUrl && <img src={qrCodeUrl} alt="QR Code" />}
          </form>
      )}
    </div>
  );
};

export default ExistingUserForm;
