import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RegistrationList = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/customers');
        setCustomers(response.data);
      } catch (error) {
        alert('Error fetching data');
      }
    };

    fetchCustomers();
  }, []);

  const printCustomer = (customer) => {
    const printWindow = window.open('', '_blank');
    printWindow.document.open();
    printWindow.document.write(`
      <html>
      <head>
        <title>Print</title>
        <style>
          /* General styles */
          body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            color: #333;
          }
          .print-container {
            padding: 20px;
            max-width: 800px;
            margin: 0 auto;
            border: 1px solid #ddd;
            border-radius: 5px;
            background: #f9f9f9;
          }
          .print-header {
            text-align: center;
            margin-bottom: 20px;
          }
          .print-header .header-top {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 10px;
          }
          .print-header .header-logo img {
            width: 100px; /* Adjust size as needed */
          }
          .print-header .header-title h1 {
            font-size: 24px;
            margin: 0;
          }
          .print-header .header-title h2 {
            font-size: 18px;
            margin: 0;
            color: #666;
          }
          .print-header .header-middle h3 {
            font-size: 16px;
            margin: 0;
            color: #444;
          }
          .print-details {
            margin-top: 20px;
          }
          .print-details p {
            font-size: 14px;
            line-height: 1.6;
            margin: 5px 0;
          }
          .print-details p strong {
            color: #000;
          }
        </style>
      </head>
      <body>
        <div class="print-container">
          <div class="print-header">
            <div class="header-top">
              <div class="header-logo">
                <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdY4U3hWMpmts0iOuvPulvNNr-af-zy85dgQ&s/100" alt="Left Logo" />
              </div>
              <div class="header-title">
                <h1>Jain Engineer's Society Aurangabad</h1>
                <h2>JESA JESA-Nx</h2>
              </div>
              <div class="header-logo">
                <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdY4U3hWMpmts0iOuvPulvNNr-af-zy85dgQ&s/100" alt="Right Logo" />
              </div>
            </div>
            <div class="header-middle">
              <h3>Startup Edge: Mastering AI-Driven Video Creation</h3>
            </div>
          </div>
          <div class="print-details">
            <p><strong>Name:</strong> ${customer.name}</p>
            <p><strong>Place:</strong> ${customer.place}</p>
            <p><strong>Phone:</strong> ${customer.phone}</p>
            <p><strong>Source:</strong> ${customer.source}</p>
            <p><strong>Members:</strong> ${customer.members}</p>
            <p><strong>Occupation:</strong> ${customer.occupation}</p>
          </div>
        </div>
      </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };
  

  return (
    <div className="list-container">
      <header>
        <div className="header-content">
          <h1>Submitted Registrations</h1>
        </div>
      </header>
      <ul>
        {customers.map((customer) => (
          <li key={customer._id}>
            {customer.name} - {customer.place} - {customer.phone} - {customer.source} - {customer.members} - {customer.occupation}
            <button onClick={() => printCustomer(customer)}>Print</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RegistrationList;
