// src/components/CustomerDetails.js
import React, { useState } from 'react';
import axios from 'axios';
import QRCodeDisplay from './QRCodeDisplay';

const CustomerDetails = () => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [customerData, setCustomerData] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      const response = await axios.get('http://localhost:4000/api/customer/by-name-phone', {
        params: {
          name,
          phone,
        },
      });
      setCustomerData(response.data);
    } catch (err) {
      setError('Error retrieving customer details');
      setCustomerData(null);
    }
  };

  return (
    <div>
      <h1>Get Customer Details</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Phone Number:
            <input
              type="text"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </label>
        </div>
        <button type="submit">Get Details</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {customerData && (
        <div>
          <h2>Customer Details</h2>
          <pre>{JSON.stringify(customerData, null, 2)}</pre>
          <QRCodeDisplay value={JSON.stringify(customerData)} />
        </div>
      )}
    </div>
  );
};

export default CustomerDetails;
